import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function Main() {
    const [time, setTime] = useState('');

    const fetchTime = () => {
        console.log(time)
        axios.get('http://localhost:8080/test/time')
            .then(response => {
                setTime(response.data);
            })
            .catch(error => {
                console.error(error);
                if (!time) {
                    setTime('연결되지 않았습니다');
                }
            });
    };

    useEffect(() => {
        fetchTime();
    }, []);

    return (
        <div>
            <h1>Main 페이지</h1>
            {time && <p>현재 시간: {time}</p>}
            {!time && <p>연결되지 않았습니다</p>}
            <button onClick={fetchTime}>시간 갱신</button>
        </div>
    );
}
