import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function Update() {
    const [searchResults, setSearchResults] = useState([]);
    const [updatedItem, setUpdatedItem] = useState({ pid: '', pt_nm: '' });

    const handleSearch = () => {
        axios.post('http://localhost:8080/test/selectSearch', {
            pid: null,
            pt_nm: null
        })
            .then(response => {
                setSearchResults(response.data);
            })
            .catch(error => {
                console.error(error);
            });
    };

    const handleUpdateItem = (pid) => {
        axios.post('http://localhost:8080/test/update', {
            pid: pid,
            pt_nm: updatedItem.pt_nm
        })
            .then(response => {
                // 업데이트 후 목록을 갱신하기 위해 검색을 실행
                handleSearch();
            })
            .catch(error => {
                console.error(error);
            });
    };

    useEffect(() => {
        handleSearch();
    }, []);

    return (
        <div>
            <br />
            <h2>아이템 업데이트</h2>
            <ul>
                {searchResults.map((item) => (
                    <li key={item.pid}>
                        <span>pid: {item.pid}, pt_nm: {item.pt_nm}</span>
                        <input
                            type="text"
                            value={updatedItem.pt_nm}
                            onChange={(e) => setUpdatedItem({ pid: item.pid, pt_nm: e.target.value })}
                        />
                        <button onClick={() => handleUpdateItem(item.pid)}>업데이트</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}
